import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';

import {Modal, Nav, Tab} from 'react-bootstrap';
import SignUpForm from './SignUp';
import LoginForm from './Login';
import Auth from '../utils/auth';

import '../styles/Navbar.css';

export default function Navbar() {
  const [showModal, setShowModal] = useState(false);
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);
  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };
  useEffect(() => {
    showButton();
  }, []);
  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <h1 className='navbar-logo' onClick={closeMobileMenu}>
            Health Hacker
          </h1>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
         <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Nav.Link  as={Link} to='/' className=' nav-links' onClick={closeMobileMenu}>
                Home
              </Nav.Link>
            </li>
            {Auth.loggedIn() ? (
                <>
                <li className='nav-item'>
                  <Nav.Link as={Link} to='/myJourney' className='nav-links' onClick={closeMobileMenu}>
                    My Journey
                  </Nav.Link>
                  <Nav.Link onClick={Auth.logout}>{button && <Button buttonStyle='btn--outline'>LOGOUT</Button>}</Nav.Link>
                </li>
                </>
              ) : (
                <Nav.Link onClick={() => setShowModal(true)}>{button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}</Nav.Link>
              )
              } 
          </ul>
        </div>
      </nav>
            {/* set modal data up */}
      <Modal
        size='lg'
        show={showModal}
        onHide={() => setShowModal(false)}
        aria-labelledby='signup-modal'>
        {/* tab container to do either signup or login component */}
        <Tab.Container defaultActiveKey='login'>
          <Modal.Header closeButton>
            <Modal.Title id='signup-modal'>
              <Nav variant='pills'>
                <Nav.Item>
                  <Nav.Link eventKey='login'>Login</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey='signup'>Sign Up</Nav.Link>
                </Nav.Item>
              </Nav>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Tab.Content>
              <Tab.Pane eventKey='login'>
                <LoginForm handleModalClose={() => setShowModal(false)} />
              </Tab.Pane>
              <Tab.Pane eventKey='signup'>
                <SignUpForm handleModalClose={() => setShowModal(false)} />
              </Tab.Pane>
            </Tab.Content>
          </Modal.Body>
        </Tab.Container>
      </Modal>
    </>
  );
}
