import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';

import {Container, Modal} from 'react-bootstrap';
import SignupForm from './SignUp';
import LoginForm from './Login';
import Auth from '../utilis/auth';

import '../styles/Navbar.css';

function Navbar() {
  const [showModal, setShowModal] = useState(false);
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            Health Hacker
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className='nav-item'>
                  <Link to='/myJourney' className='nav-links' onClick={closeMobileMenu}>
                    My Journey
                  </Link>
            </li>
            {Auth.loggedIn() ? (
                <>
                <li className='nav-item'>
                  <Link as={Link} to='/myJourney' className='nav-links' onClick={closeMobileMenu}>
                    My Journey
                  </Link>
                </li>
                </>
              ) : (
                <Link onClick={() => setShowModal(true)}>Login/Sin</Link>
            )}

            <li>
              <Link
                to='/sign-up'
                className='nav-links-mobile'
                onClick={closeMobileMenu}
              >
                Sign Up
              </Link>
            </li>
          </ul>
          {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
